import React, { useState } from 'react'

import { NotesList } from './NotesList'
import { NoteForm } from './NoteForm'

export const App = (props) => {
    const { service } = props

    const [notes, setNotes] = useState([])
    const [selected, setSelected] = useState(null)

    // (!) Get notes from service
    let allNotes = service.getNotes();

    // Select new empty note
    function newNote(){
        setSelected({ note: {title:'', text:''}})
    }

    // Set note as selected
    function onSelect(note){
        setSelected(note);
    }

    // Save note to service
    async function onSubmit(note){
        await service.saveNote(note);
        setNotes(service.getNotes())
    }

    // Unselect note
    function onCancel(){
        setSelected(null);
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-12">
                    <h1>React notes</h1>
                </div>
            </div>
            <div className="row">
                <div className="col-md-4">
                    <NotesList notes={[]} />
                </div>
                <div className="col-md-8">
                    <NoteForm onClick={() => this.onSubmit()} />
                    <div><button id="new-note" onClick={() => this.newNote()}>New Note</button></div>
                </div>
            </div>
        </div>
    )
}
