import React from 'react'

export const NotesList = (props) => {
    const { notes = [] , onSelect} = props
    let isActive = false;

    console.log('notes:' + notes.toString())

    function handleSelectNote(note) {
        isActive = true;
        onSelect(note)
    }

    return <div className="list-group">
        <>
            {notes.length > 0 && notes.map(note => (
            <div data-testid={note.id} className={isActive ? "list-group-item active" : "list-group-item"} onClick={() => handleSelectNote(note)}>{note.title}</div>
            ))}
        </>
    </div>
}
