import React from 'react'

export const NoteForm = (props) => {
    const { note = { title: '', text: '' }, onSubmit, onCancel } = props;

    function handleOnChange(key, event) {
        if (key === "title") {
            note.title = event.target.input;
        } else if (key === "text") {
            note.text = event.target.input
        }
    }

    function handleOnSubmit() {
        onSubmit(note)
    }

    function handleCancelNote() {
        onCancel()
    }

    return <form>
        <div className="form-group">
            <label>Title:</label>
            <input
                className="form-control"
                data-testid="input-title"
                name="title"
                onChange={(e) => handleOnChange('title', e)}
            />
        </div>
        <div className="form-group">
            <label>Note:</label>
            <textarea
                className="form-control"
                data-testid="input-text"
                name="text"
                onChange={(e) => handleOnChange('text', e)}
            />
        </div>
        <div className="form-group">
            <input
                type="button"
                data-testid="cancel-note"
                className="btn btn-default pull-right"
                value="Cancel"
                onClick={handleCancelNote}
            />
            <input
                type="submit"
                data-testid="save-note"
                className="btn btn-default pull-right"
                value="Save"
                onClick={handleOnSubmit}
            />
        </div>
    </form>
}
