import React, { useEffect, useState } from 'react'

import { NotesList } from './NotesList'
import { NoteForm } from './NoteForm'

export const App = (props) => {
    const { service } = props

    const [notes, setNotes] = useState([])
    const [selected, setSelected] = useState(null)

    let allNotes;

    // (!) Get notes from service
    useEffect(() => {
        const getInitialNotes= async () => {
            allNotes = await service.getNotes();
            setNotes(allNotes)
        }

        getInitialNotes()
    }, [service])

    // Select new empty note
    function newNote(){
        setSelected({id: '', title:'', text:''})
    }

    // Set note as selected
    function onSelect(note){
        setSelected(note);
    }

    // Save note to service
    async function onSubmit(note){
        await service.saveNote(note);
        allNotes = await service.getNotes();
        setNotes(allNotes)
    }

    // Unselect note
    function onCancel(){
        setSelected(null);
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-12">
                    <h1>React notes</h1>
                </div>
            </div>
            <div className="row">
                <div className="col-md-4">
                    <NotesList notes={notes} />
                </div>
                <div className="col-md-8">
                    <NoteForm note={selected} onCancel={onCancel} onSubmit={onSubmit}/>
                    <div><button id="new-note" onClick={() => newNote()}>New Note</button></div>
                </div>
            </div>
        </div>
    )
}
